"use strict";

var TraverseSnapshot = require("cruks-lib-traverse-tracker").TraverseSnapshot;

/**
 * @auguments cruks-lib-traverse-tracker/TraverseSnapshot
 * @constructor
 * @param {*} item element to be checked against
 * @param {cruks-lib-verbosity-indicator/Verbosity} verbosity message eloquence
 */
function AssertionSnapshot(item) {
    TraverseSnapshot.call(this, item);
}
AssertionSnapshot.prototype = Object.create(TraverseSnapshot.prototype);

module.exports = AssertionSnapshot;
