"use strict";

var path = require("path"),
    Assertion = require(path.join(__dirname, "/../Assertion")),
    StringBuilder = require("cruks-lib-string-builder").StringBuilder;

/**
 * @auguments cruks-lib-config/Testable/Assertion
 * @constructor
 */
function AnythingAssertion() {
    Assertion.call(this);
}
AnythingAssertion.prototype = Object.create(Assertion.prototype);

AnythingAssertion.prototype.getExpectationBaseMessage = function (item, verbosity, isNegated) {
    var message = new StringBuilder(" ", verbosity);

    if (isNegated) {
        message.append("nothing");
    } else {
        message.append("anything");
    }

    return message;
};

AnythingAssertion.prototype.test = function (item /*, verbosity, tracker*/ ) {
    return item;
};

module.exports = AnythingAssertion;
