"use strict";

var path = require("path"),
    AnythingAssertion = require(path.join(__dirname, "/../Anything"));

/**
 * @auguments cruks-lib-config/Testable/Assertion/Anything
 * @constructor
 * @param {*} dft default assertion value
 */
function DefaultAssertion(dft) {
    AnythingAssertion.call(this);

    this.default(dft);
}
DefaultAssertion.prototype = Object.create(AnythingAssertion.prototype);

module.exports = DefaultAssertion;
