"use strict";

var path = require("path"),
    Assertion = require(path.join(__dirname, "/../Assertion")),
    FunctionTypeAssertion = require(path.join(__dirname, "/Type/Function")),
    StringBuilder = require("cruks-lib-string-builder").StringBuilder;

/**
 * @auguments cruks-lib-config/Testable/Assertion
 * @constructor
 * @param {Function} Parent parent object constructor
 */
function InstanceOfAssertion(Parent) {
    Assertion.call(this);

    this.Parent = (new FunctionTypeAssertion()).assert(Parent);
}
InstanceOfAssertion.prototype = Object.create(Assertion.prototype);

InstanceOfAssertion.prototype.getExpectationBaseMessage = function (item, verbosity, isNegated) {
    var message = new StringBuilder(" ", verbosity);

    if (isNegated) {
        message.append("not");
    }

    return message.append("instance of").appendDump(this.Parent);
};

InstanceOfAssertion.prototype.test = function (Child, verbosity, tracker) {
    if (Child instanceof this.Parent) {
        return Child;
    }

    this.throwAssertionFailureException(Child, verbosity, tracker);
};

module.exports = InstanceOfAssertion;
