"use strict";

var path = require("path"),
    NumberAssertion = require(path.join(__dirname, "/../Number")),
    StringBuilder = require("cruks-lib-string-builder").StringBuilder;

/**
 * @auguments cruks-lib-config/Testable/Assertion/Type/Number
 * @constructor
 */
function IntegerAssertion() {
    NumberAssertion.call(this);
}
IntegerAssertion.prototype = Object.create(NumberAssertion.prototype);

IntegerAssertion.prototype.getExpectationBaseMessage = function (item, verbosity, isNegated) {
    var message = new StringBuilder(" ", verbosity);

    if (isNegated) {
        message.append("not");
    }

    return message.append("integer");
};

IntegerAssertion.prototype.test = function (item, verbosity, tracker) {
    item = NumberAssertion.prototype.test.call(this, item, verbosity, tracker);

    if (parseFloat(item) === parseInt(item, 10)) {
        return item;
    }

    this.throwAssertionFailureException(item, verbosity, tracker);
};

module.exports = IntegerAssertion;
