"use strict";

var path = require("path"),
    EnumAssertion = require(path.join(__dirname, "/../Enum")),
    TypeAssertion = require(path.join(__dirname, "/../Type"));

/**
 * @auguments cruks-lib-config/Testable/Assertion/Type
 * @constructor
 */
function PrimitiveAssertion() {
    TypeAssertion.call(this, new EnumAssertion(["boolean", "number", "string"]));

    this.typeName = "primitive type";
}
PrimitiveAssertion.prototype = Object.create(TypeAssertion.prototype);

module.exports = PrimitiveAssertion;
