"use strict";

var path = require("path"),
    SymbolShim = require("polacks-shim-es6").Symbol,
    TypeAssertion = require(path.join(__dirname, "/../Type"));

/**
 * @auguments cruks-lib-config/Testable/Assertion/Type
 * @constructor
 */
function SymbolAssertion() {
    TypeAssertion.call(this, "symbol");
}
SymbolAssertion.prototype = Object.create(TypeAssertion.prototype);

SymbolAssertion.prototype.test = function (item, verbosity, tracker) {
    if (SymbolShim.isSymbol(item)) {
        // this is a fallback for unsupported environments
        return item;
    }

    return TypeAssertion.prototype.test.call(this, item, verbosity, tracker);
};

module.exports = SymbolAssertion;
