"use strict";

var path = require("path"),
    BaseConfiguration = require(path.join(__dirname, "/../BaseConfiguration"));

/**
 * This is the base abstract class for implementing reusable configuration
 * objects.
 *
 * @auguments cruks-lib-config/BaseConfiguration
 * @constructor
 */
function Configuration() {
    BaseConfiguration.call(this);
}
Configuration.prototype = Object.create(BaseConfiguration.prototype);

module.exports = Configuration;
