"use strict";

/*global describe: false, it: false */

var assert = require("chai").assert,
    AnythingAssertion = require(process.env.PACKAGE_ROOT + "/cruks-lib-config/Testable/Assertion/Anything");

describe("cruks-lib-config/Testable/Assertion/Anything", function () {
    describe("#assert()", function () {
        it("expects anything", function () {
            var aa = new AnythingAssertion(),
                fs = 3;

            assert.strictEqual(fs, aa.assert(fs));
        });
    });

    describe("#default()", function () {
        it("defaults to given value", function () {
            var aa = new AnythingAssertion().default(undefined);

            assert.strictEqual(undefined, aa.assert(null));
        });
    });
});
