"use strict";

/*global describe: false, it: false */

var assert = require("chai").assert,
    DefaultAnythingAssertion = require(process.env.PACKAGE_ROOT + "/cruks-lib-config/Testable/Assertion/Anything/Default");

describe("cruks-lib-config/Testable/Assertion/Anything/Default", function () {
    describe("#assert()", function () {
        var da = new DefaultAnythingAssertion("foo");

        it("picks last default value", function () {
            assert.strictEqual("baz", da.default("bar").default("baz").assert());
        });
    });
});
