"use strict";

/*global describe: false, it: false */

var assert = require("chai").assert,
    TypeAssertion = require(process.env.PACKAGE_ROOT + "/cruks-lib-config/Testable/Assertion/Type");

describe("cruks-lib-config/Testable/Assertion/Type", function () {
    describe("#assert()", function () {
        it("asserts that given item represents given type", function () {
            /* eslint func-style: 0 */

            var ta = new TypeAssertion("function"),
                fs = function () {};

            assert.strictEqual(fs, ta.assert(fs));
        });
    });

    describe("#default()", function () {
        it("picks default value", function () {
            function dft() {
            }

            assert.strictEqual(dft, new TypeAssertion("function").default(dft).assert(null));
        });

        it("picks input value", function () {
            function dft() {
            }
            function input() {
            }

            assert.strictEqual(input, new TypeAssertion("function").default(dft).assert(input));
        });
    });
});
