"use strict";

/*global describe: false, it: false */

var assert = require("chai").assert,
    BooleanTypeAssertion = require(process.env.PACKAGE_ROOT + "/cruks-lib-config/Testable/Assertion/Type/Boolean");

describe("cruks-lib-config/Testable/Assertion/Type/Boolean", function () {
    describe("#default()", function () {
        var ta = (new BooleanTypeAssertion()).default(true);

        it("checks input type", function () {
            assert.strictEqual(true, ta.assert(null));
        });

        it("picks input value", function () {
            assert.strictEqual(false, ta.assert(false));
        });

        it("picks default value", function () {
            assert.strictEqual(true, ta.assert(""));
        });
    });
});
