"use strict";

/*global describe: false, it: false */

var assert = require("chai").assert,
    FunctionTypeAssertion = require(process.env.PACKAGE_ROOT + "/cruks-lib-config/Testable/Assertion/Type/Function");

describe("cruks-lib-config/Testable/Assertion/Type/Function", function () {
    function dft() {
    }

    describe("#default()", function () {
        var fta = new FunctionTypeAssertion().default(dft).optional();

        it("picks input value", function () {
            function correct() {
            }

            assert.strictEqual(correct, fta.assert(correct));
        });

        it("picks default value", function () {
            assert.strictEqual(dft, fta.assert(false));
        });
    });
});
