"use strict";

/*global describe: false, it: false */

var assert = require("chai").assert,
    PrimitiveTypeAssertion = require(process.env.PACKAGE_ROOT + "/cruks-lib-config/Testable/Assertion/Type/Primitive");

describe("cruks-lib-config/Testable/Assertion/Type/Primitive", function () {
    var pta = new PrimitiveTypeAssertion();

    describe("#assert()", function () {
        it("accepts boolean", function () {
            assert.strictEqual(true, pta.assert(true));
        });

        it("accepts number", function () {
            assert.strictEqual(5, pta.assert(5));
        });

        it("accepts string", function () {
            assert.strictEqual("foo", pta.assert("foo"));
        });
    });
});
