"use strict";

/*eslint new-cap: 0 */
/*global describe: false, it: false */

var assert = require("chai").assert,
    AssertionFailureException = require(process.env.PACKAGE_ROOT + "/cruks-lib-config/Exception/AssertionFailure"),
    SymbolAssertion = require(process.env.PACKAGE_ROOT + "/cruks-lib-config/Testable/Assertion/Type/Symbol"),
    SymbolShim = require("polacks-shim-es6").Symbol;

describe("cruks-lib-config/Testable/Assertion/Type/Symbol", function () {
    var ka = new SymbolAssertion();

    describe("#assert()", function () {
        it("accepts symbols", function () {
            var s = SymbolShim();

            assert.strictEqual(ka.assert(s), s);
        });

        it("fails when something else than symbol is given", function () {
            assert.throws(function () {
                ka.assert({});
            }, AssertionFailureException, "expected symbol but got object");
        });
    });
});
