"use strict";

/*global describe: false, it: false */

var assert = require("chai").assert,
    expect = require(process.env.PACKAGE_ROOT + "/cruks-lib-config").expect;

describe("cruks-lib-config/expect", function () {
    it("exports assertions", function () {
        assert.isFunction(expect.anything);
        assert.isFunction(expect.array);
        assert.isFunction(expect.boolean);
        assert.isFunction(expect.childOf);
        assert.isFunction(expect.custom);
        assert.isFunction(expect.date);
        assert.isFunction(expect.empty);
        assert.isFunction(expect.enum);
        assert.isFunction(expect.every);
        assert.isFunction(expect.exact);
        assert.isFunction(expect.function);
        assert.isFunction(expect.instanceOf);
        assert.isFunction(expect.integer);
        assert.isFunction(expect.key);
        assert.isFunction(expect.map);
        assert.isFunction(expect.number);
        assert.isFunction(expect.not);
        assert.isFunction(expect.object);
        assert.isFunction(expect.optional);
        assert.isFunction(expect.primitive);
        assert.isFunction(expect.regexp);
        assert.isFunction(expect.schema);
        assert.isFunction(expect.some);
        assert.isFunction(expect.string);
        assert.isFunction(expect.symbol);
        assert.isFunction(expect.type);
    });
});
