"use strict";

/*global describe: false, it: false */

var assert = require("chai").assert,
    config = require(process.env.PACKAGE_ROOT + "/cruks-lib-config");

describe("cruks-lib-config", function () {
    it("exports modules correctly", function () {
        assert.ok(config.Assertion);
        assert.ok(config.AssertionFailureException);
        assert.ok(config.Configuration);
        assert.ok(config.Settings);
        assert.ok(config.expect);
    });
});
