"use strict";

/**
 * @constructor
 * @param {cruks-lib-string-builder/FragmentStrategy} strategy
 *     message fragment builder
 * @param {String} [separator] fragment separator
 */
function Fragment(strategy, separator) {
    this.strategy = strategy;
    this.separator = separator;
}

Fragment.prototype.toString = function () {
    return String(this.separator) + String(this.strategy);
};

module.exports = Fragment;
