"use strict";

var VerbosityIndicator = require("cruks-lib-verbosity-indicator").VerbosityIndicator;

/**
 * @constructor
 * @param {String} fragment message fragment
 * @param {cruks-lib-string-builder/VerbosityIndicator} [verbosity] normal by default
 */
function FragmentStrategy(fragment, verbosity) {
    this.fragment = fragment;
    this.verbosity = new VerbosityIndicator(verbosity);
}

FragmentStrategy.prototype.toString = function () {
    return this.fragment;
};

module.exports = FragmentStrategy;
