"use strict";

/* global describe: false, it: false */

var assert = require("assert"),
    FormatFragmentStrategy = require(global.paths.root + "/cruks-lib-string-builder/FragmentStrategy/Format"),
    VerbosityIndicator = require("cruks-lib-verbosity-indicator").VerbosityIndicator;

describe("cruks-lib-string-builder/StringBuilder", function () {
    describe("#toString()", function () {
        it("formats string", function () {
            var vd = new FormatFragmentStrategy("{%d}", [1]);

            assert.strictEqual("{1}", vd.toString());
        });

        it("respects quiet verbosity", function () {
            var vd = new FormatFragmentStrategy("{%d}", [1], VerbosityIndicator.VERBOSITY_QUIET);

            assert.strictEqual("", vd.toString());
        });
    });
});
