const fs = require('fs');
const write = require('write');
var manifestPath;
var manifestContent;

process.on('message', function (data) {
    if (data.manifestPath) {
        manifestPath = data.manifestPath;
        manifestContent = data.manifestContent;
        return;
    }
    if (data.error) {
        console.log(manifestPath);
        write(manifestPath, manifestContent, function (err) {
            if (err) {
                throw new Error('writeFile:' + err);
            } else {
                process.exit(1);
            }
        });
        return;
    }
    process.exit(1);
});